/* Procedure structure for procedure `proc_record_game_user_login` */

/*!50003 DROP PROCEDURE IF EXISTS  `proc_record_game_user_login` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `proc_record_game_user_login`(
	IN _serverid INT, 
	IN _account VARCHAR(64) CHARACTER SET utf8, 
	IN _platform VARCHAR(8) CHARACTER SET utf8, 
	IN _mac VARCHAR(64) CHARACTER SET utf8, 
	IN _ip INT UNSIGNED, 
	IN _version INT UNSIGNED, 
	IN _new INT,
	IN _sbid VARCHAR(16) CHARACTER SET utf8
)
    SQL SECURITY INVOKER
BEGIN
	DECLARE _spid VARCHAR(16) DEFAULT "";
	DECLARE _pos INT DEFAULT 0;
	DECLARE _userid INT DEFAULT 0;
	DECLARE _createTime DATETIME;
	DECLARE _createserver INT DEFAULT 0;
	DECLARE _loginTime DATETIME;
	DECLARE _loginTime2 DATETIME;
	DECLARE _backMask BIGINT UNSIGNED DEFAULT 0;
	DECLARE _createLoginTime DATETIME;
	DECLARE _is_cross TINYINT UNSIGNED DEFAULT 0;
	
		
	SET _pos = LOCATE('&', _account);
	IF _pos > 0 THEN
		SET _spid = SUBSTR(_account, _pos + 1, 16);
		IF _spid IS NULL THEN
			SET _spid = "";
		END IF;
	END IF;
	
	IF _new = 1 THEN
		INSERT INTO game_users (account, serverid, createtime, createplatform, createspid,createsbid, macaddr, logintime, logouttime, loginplatform,loginserverid)
		VALUES (_account, _serverid, NOW(), _platform, _spid,_sbid, _mac, NOW(), '1970-1-1', _platform,_serverid);
		SET _createserver=_serverid;
	ELSE
		SELECT userid, createtime, logintime,serverid INTO _userid, _createTime, _loginTime,_createserver FROM game_users WHERE account = _account LIMIT 1;
		
		IF (_userid > 0) THEN
			SET _createLoginTime=_loginTime;
			SET _backMask = TIMESTAMPDIFF(DAY,DATE(_createTime),CURDATE());
			IF (_backMask > 0) AND (_backMask < 60) THEN
				SET _backMask = 1 << (_backMask - 1);
			ELSE
				SET _backMask=0;		
			END IF;
			
			IF (_createserver!=_serverid) THEN 
				SET _is_cross=1;
			END IF;
			
			UPDATE game_users SET logintime = NOW(), loginplatform = _platform,loginserverid=_serverid,`is_cross`=_is_cross, dayback = dayback | _backMask WHERE userid = _userid LIMIT 1;
						
			IF (DATE(_loginTime) != CURDATE()) THEN
				
				IF (_createserver!=_serverid) THEN 
					INSERT INTO game_atvremain (serverid,spid,sbid, oldusers,oldusers_full,logdate) 
					VALUES (_serverid,_spid,_sbid, 1,0, CURDATE()) 
					ON DUPLICATE KEY UPDATE oldusers=oldusers+1;
				ELSE	
					INSERT INTO game_atvremain (serverid,spid,sbid, oldusers,oldusers_full,logdate) 
					VALUES (_serverid,_spid,_sbid, 1,1, CURDATE()) 
					ON DUPLICATE KEY UPDATE oldusers=oldusers+1,oldusers_full=oldusers_full+1;			
				END IF;
				
												
			END IF;
			
			-- 跨服的话取登陆服的上次登陆时间
			IF (_createserver!=_serverid) THEN 
			
				SELECT `logdate` INTO _loginTime2 FROM game_login_log WHERE `server`=_serverid AND account=_account AND logdate BETWEEN CURDATE() AND NOW() ORDER BY logdate DESC LIMIT 1;
				
				IF (_loginTime2 IS NULL) THEN 
					INSERT INTO game_atvremain (serverid,spid,sbid, oldusers,oldusers_full,logdate) 
					VALUES (_serverid,_spid,_sbid, 0,1, CURDATE()) 
					ON DUPLICATE KEY UPDATE oldusers_full=oldusers_full+1;
				END IF;
					
			END IF;
			
			
		END IF;
	END IF;
	
	INSERT INTO game_login_log (SERVER, account, spid,sbid, platform, ipaddr, macaddr, cversion, logdate,createserver)
	VALUES (_serverid, _account, _spid,_sbid, _platform, INET_NTOA(_ip), _mac, INET_NTOA(_version), NOW(),_createserver);
	
	
	
END */$$
DELIMITER ;

/* Procedure structure for procedure `proc_record_game_user_logout` */

/*!50003 DROP PROCEDURE IF EXISTS  `proc_record_game_user_logout` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `proc_record_game_user_logout`(
	IN _serverid INT, 
	IN _account VARCHAR(64) CHARACTER SET utf8, 
	IN _charname VARCHAR(64) CHARACTER SET utf8,
	IN _onlineSec INT
)
BEGIN
	DECLARE nTodaySec BIGINT DEFAULT 0;
	INSERT INTO game_logout_log (SERVER, account, charname, ipaddr, logdate)
	VALUES (_serverid, _account, _charname, _onlineSec, NOW());
	UPDATE game_users SET logouttime = NOW() WHERE account = _account LIMIT 1;
	SET nTodaySec = UNIX_TIMESTAMP(NOW()) - UNIX_TIMESTAMP(CURDATE());
	
	IF (_onlineSec >= nTodaySec) THEN
		INSERT INTO game_day_online_time(account, SERVER, onlinetime, logdate)
		VALUES (_account, _serverid, _onlineSec - nTodaySec, DATE_SUB(CURDATE(), INTERVAL 1 DAY))
		ON DUPLICATE KEY UPDATE onlinetime=onlinetime+(_onlineSec - nTodaySec);	
		SET _onlineSec = nTodaySec;
	END IF;
	
	IF _onlineSec > 0 THEN
		INSERT INTO game_day_online_time(account, SERVER, onlinetime, logdate)
		VALUES (_account, _serverid, _onlineSec, CURDATE())
		ON DUPLICATE KEY UPDATE onlinetime=onlinetime+_onlineSec;
	END IF;
END */$$
DELIMITER ;

